export const getMembersDataSearch = (search: string, searchType: number) => {
    let searchTypeUrl = 'email';
    if (searchType === 0) {
        searchTypeUrl = 'email';
    } else if (searchType === 1) {
        searchTypeUrl = 'first_name';
    } else if (searchType === 2) {
        searchTypeUrl = 'last_name';
    }

    const headers = {
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9..cVEXvLx0xhkXHLn_XbQj-8iU3bG3Vsn4vZbtQlD60PE'
    };
    const success = ({data}) => data;
    const error = () => fail;
    const memberUrlSearch = 'https://sandbox.glofox.com/2.0/members?' + searchTypeUrl + '=' + search;
    return axios.get(memberUrlSearch, {headers}).then(success).catch(error);
};
